table_size = len(prime_table)

def find_nth_prime(n_target, start = 2, start_n = 0):
	n = start_n
	p = start
	while n < n_target:
		p = next_prime(p)
		n += 1
	return p

def get_prime(n):
	if n < table_size:
		return prime_table[n]
	return find_nth_prime(n, start = prime_table[table_size - 1], start_n = table_size - 1)


def primes():
	n = 0
	while True:
		yield get_prime(n)
		n += 1